#include <WebServer.h>
#include "webserver.h"
#include "servos.h"

// Servos angles
int angleSG90 = 7;
int angleMG996R = 3;

WebServer server(80);

// HTML to post requests to control servos
String indexHTML() {
  return R"rawliteral(
<!DOCTYPE html>
<html lang="en">

<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Robotic Arm Control</title>
  <style>
    /* Global font style */
    body,
    button {
      font-family: Arial, sans-serif;
      /* Sets Arial as the primary font */
      margin: 0;
      padding: 0;
      box-sizing: border-box;
    }

    h1 {
      text-align: center;
    }

    img {
      display: block;
      margin: 0 auto;
      /* Centers the image horizontally */
      max-width: 100%;
      /* Ensures the image is responsive */
      height: auto;
      /* Maintains aspect ratio */
    }

    h2 {
      text-align: center;
    }

    /* Flex container for servo controls */
    .servo-control {
      display: flex;
      flex-wrap: wrap;
      justify-content: space-around;
      padding: 1rem;
    }

    /* Individual servo control blocks */
    .servo {
      flex: 1 1 200px;
      /* Flex item grows and shrinks but not smaller than 200px */
      margin: 10px;
      text-align: center;
    }

    /* Custom CSS for buttons to mimic Bootstrap style with primary blue background */
    button.servo-button {
      display: inline-block;
      font-weight: 400;
      line-height: 1.5;
      color: #ffffff;
      /* White text color for better contrast */
      text-align: center;
      text-decoration: none;
      vertical-align: middle;
      cursor: pointer;
      background-color: #007bff;
      /* Bootstrap primary blue */
      border: 1px solid #0056b3;
      /* Slightly darker blue for border */
      padding: 0.375rem 0.75rem;
      /* Padding for typical button size */
      font-size: 1rem;
      /* Standard font size */
      border-radius: 0.25rem;
      /* Rounded corners */
      transition: color 0.15s ease-in-out, background-color 0.15s ease-in-out, border-color 0.15s ease-in-out, box-shadow 0.15s ease-in-out;
      width: 100%;
      /* Make buttons full width of their container */
      margin-top: 0.5rem;
    }

    button.servo-button:hover {
      background-color: #0056b3;
      /* Darker shade of blue on hover */
      border-color: #004085;
    }

    button.servo-button:focus,
    button.servo-button:active {
      outline: none;
      box-shadow: 0 0 0 0.25rem rgba(0, 123, 255, 0.5);
      /* Light blue shadow for focus */
    }

    button.servo-button:disabled {
      opacity: 0.65;
      pointer-events: none;
    }

    /* Responsive adjustments */
    @media (max-width: 600px) {
      .servo {
        flex: 1 1 calc(33.333% - 20px);
        /* Each servo control takes one-third width on smaller screens */
      }

      button.servo-button {
        padding: 0.5rem;
        font-size: 0.9rem;
        /* Smaller font size for smaller screens */
      }
    }
  </style>
</head>

<body>
  <h1>Robotic Arm Control</h1>
  <!-- Image of the robotic arm placed here -->
  <img src="data:image/png;base64," alt="Robotic Arm">

  <script>
    function startAction(url) {
      var sendAction = function () {
        fetch(url, { method: 'POST' })
          .then(response => {
            if (!response.ok) {
              console.log('Request failed', response);
            }
          });
      };
      sendAction();
      return setInterval(sendAction, 100);
    }

    function stopAction(intervalId) {
      clearInterval(intervalId);
    }

    document.addEventListener('DOMContentLoaded', function () {
      document.querySelectorAll('.servo-button').forEach(function (button) {
        var intervalId;
        var start = function (event) {
          event.preventDefault();
          intervalId = startAction(button.getAttribute('data-action'));
        };
        var stop = function () {
          stopAction(intervalId);
        };
        button.addEventListener('mousedown', start);
        button.addEventListener('touchstart', start);
        button.addEventListener('mouseup', stop);
        button.addEventListener('mouseleave', stop);
        button.addEventListener('touchend', stop);
        button.addEventListener('touchcancel', stop);
      });
    });
  </script>

  <div class="servo-control">
    <div class="servo">
      <h2>Servo 1</h2>
      <button class='servo-button' data-action='/servo1/right'>LEFT</button>
      <button class='servo-button' data-action='/servo1/left'>RIGHT</button>
    </div>
    <div class="servo">
      <h2>Servo 2</h2>
      <button class='servo-button' data-action='/servo2/right'>UP</button>
      <button class='servo-button' data-action='/servo2/left'>DOWN</button>
    </div>
    <div class="servo">
      <h2>Servo 3</h2>
      <button class='servo-button' data-action='/servo3/left'>UP</button>
      <button class='servo-button' data-action='/servo3/right'>DOWN</button>
    </div>
    <div class="servo">
      <h2>Servo 4</h2>
      <button class='servo-button' data-action='/servo4/right'>LEFT</button>
      <button class='servo-button' data-action='/servo4/left'>RIGHT</button>
    </div>
    <div class="servo">
      <h2>Servo 5</h2>
      <button class='servo-button' data-action='/servo5/right'>UP</button>
      <button class='servo-button' data-action='/servo5/left'>DOWN</button>
    </div>
    <div class="servo">
      <h2>Servo 6</h2>
      <button class='servo-button' data-action='/servo6/right'>LEFT</button>
      <button class='servo-button' data-action='/servo6/left'>RIGHT</button>
    </div>
  </div>

</body>

</html>
)rawliteral";
}

void setupWebServer() {
  server.on("/", HTTP_GET, []() {
    server.send(200, "text/html", indexHTML());
  });

  // Servo 1 control by ID and angle
  server.on("/servo1/left", HTTP_POST, []() {
    rotateServoByID(1, -angleMG996R);
  });
  server.on("/servo1/right", HTTP_POST, []() {
    rotateServoByID(1, angleMG996R);
  });

  // Servo 2 control by ID and angle
  server.on("/servo2/left", HTTP_POST, []() {
    rotateServoByID(2, -angleMG996R);
  });
  server.on("/servo2/right", HTTP_POST, []() {
    rotateServoByID(2, angleMG996R);
  });

  // Servo 3 control by ID and angle
  server.on("/servo3/left", HTTP_POST, []() {
    rotateServoByID(3, -angleMG996R);
  });
  server.on("/servo3/right", HTTP_POST, []() {
    rotateServoByID(3, angleMG996R);
  });

  // Servo 4 control by ID and angle
  server.on("/servo4/left", HTTP_POST, []() {
    rotateServoByID(4, -angleSG90);
  });
  server.on("/servo4/right", HTTP_POST, []() {
    rotateServoByID(4, angleSG90);
  });

  // Servo 5 control by ID and angle
  server.on("/servo5/left", HTTP_POST, []() {
    rotateServoByID(5, -angleSG90);
  });
  server.on("/servo5/right", HTTP_POST, []() {
    rotateServoByID(5, angleSG90);
  });

  // Servo 6 control by ID and angle
  server.on("/servo6/left", HTTP_POST, []() {
    rotateServoByID(6, -angleSG90);
  });
  server.on("/servo6/right", HTTP_POST, []() {
    rotateServoByID(6, angleSG90);
  });

  server.begin();
}

void handleClientRequests() {
  server.handleClient();
}

